mapper.select:
SELECT qnano, memberno, categoryno, title, quest, rdate, FROM qna 
WHERE categoryno = #{categoryno}
ORDER BY qnano DESC

SELECT qnano, memberno, categoryno, title, quest, rdate, FROM qna 
WHERE memberno = #{memberno}
ORDER BY qnano DESC

SELECT COUNT(*) as cnt FROM qna
WHERE categoryno=#{categoryno}
WHERE categoryno=#{categoryno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(quest) LIKE '%' || UPPER(#{word}) || '%')

mapper.update:
UPDATE qna
SET title=#{title}, quest=#{quest},
WHERE qnano = #{qnano}

mapper.select:
SELECT COUNT(*) as cnt 
FROM qna 
WHERE categoryno=#{categoryno}

SELECT COUNT(*) as cnt 
FROM qna 
WHERE memberno=#{memberno}
