mapper.select:
SELECT qnano, memberno, categoryno, title, quest, rdate, FROM qna 
WHERE categoryno = #{categoryno}
ORDER BY qnano DESC

SELECT qnano, memberno, categoryno, title, quest, rdate, FROM qna 
WHERE memberno = #{memberno}
ORDER BY qnano DESC

mapper.update:
UPDATE qna
SET title=#{title}, quest=#{quest},
WHERE qnano = #{qnano}

mapper.select:
SELECT COUNT(*) as cnt 
FROM qna 
WHERE categoryno=#{categoryno}

SELECT COUNT(*) as cnt 
FROM qna 
WHERE memberno=#{memberno}
